import './BrushBarChart.css';
import {
    BarChart,
    Bar,
    ReferenceLine,
    Legend,
    XAxis,
    YAxis,
    Tooltip,
    CartesianGrid,
    ResponsiveContainer,
} from 'recharts';
import PropTypes from 'prop-types';

const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
        return (
            <div className="tooltip">
                <p className="tooltip-label">{`${payload[0].value}Kg`}</p>
                <p className="tooltip-label">{`${payload[1].value}Kcal`}</p>
            </div>
        );
    }
    return null;
};

const CustomTick = ({ index, x, y }) => {
    return (
        <text x={x - 5} y={y + 20}>
            {index + 1}
        </text>
    );
};

function BrushBarChart({ data }) {
    const daysNumbers = () => {

        return data.map((index) => index + 1);

    }
    return (
        <ResponsiveContainer width="100%" height={180} id="brushBarChart" >

            <BarChart
                data={data}
                margin={{
                    top: 5,
                    right: 30,
                    left: 20,
                    bottom: 5,

                }}
                maxBarSize={8}
                barGap={"-100"}
            >
                <CartesianGrid strokeDasharray="2 2" vertical={false} />
                <XAxis
                    dataKey={daysNumbers}
                    padding={{ left: -22, right: -22 }}
                    tickLine={false}
                    tickMargin={15}
                />
                <YAxis
                    yAxisId="kg"
                    dataKey="kilogram"
                    domain={["dataMin - 2", "dataMax + 1"]}
                    allowDecimals={false}
                    dx={30}
                    orientation="right"
                    axisLine={false}
                    tickLine={false}
                />
                <YAxis
                    yAxisId="cal"
                    dataKey="calories"
                    hide={true}
                    orientation="left"
                    axisLine={false}
                    tickLine={false}
                />
                <Tooltip
                    animationEasing="ease-out"
                    content={<CustomTooltip />}
                    offset={40}
                />
                <Bar
                    yAxisId="kg"
                    dataKey="kilogram"
                    fill="#282D30"
                    radius={[10, 10, 0, 0]}
                />
                <Bar
                    yAxisId="cal"
                    dataKey="calories"
                    fill="var(--primary-color)"
                    radius={[10, 10, 0, 0]}
                />
            </BarChart>
        </ResponsiveContainer>
    );
}

BrushBarChart.propTypes = {
    data: PropTypes.array
}

export default BrushBarChart;
