import './BrushBarChart.css';
import {
    BarChart,
    Bar,
    ReferenceLine,
    XAxis,
    YAxis,
    Tooltip,
    CartesianGrid,
    ResponsiveContainer,
} from 'recharts';
import PropTypes from 'prop-types';

const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
        return (
            <div className="tooltip">
                <p className="tooltip-label">{`${payload[0].value}Kg`}</p>
                <p className="tooltip-label">{`${payload[1].value}Kcal`}</p>
            </div>
        );
    }
    return null;
};

const CustomTick = ({ index, x, y }) => {
    return (
        <text x={x - 5} y={y + 20}>
            {index + 1}
        </text>
    );
};

function BrushBarChart({ data }) {
    return (
        <ResponsiveContainer height={180} width="100%" id="brushBarChart">
            <BarChart
                data={data}
                margin={{
                    top: 5,
                    right: 30,
                    left: 20,
                    bottom: 5,
                }}
            >
                <CartesianGrid
                    strokeDasharray="2 2"
                    vertical={false}
                />
                {/* <XAxis datakey={daysNumbers} scale='point' tickLine={false} tick={<CustomTick />} stroke='white' /> */}
                <XAxis scale='point' tickLine={false} tick={<CustomTick />} stroke='white' />
                {/* <YAxis
                    yAxisId="kg"
                    dataKey="kilogram"
                    domain={["dataMin - 2", "dataMax + 1"]}
                    allowDecimals={false}
                    dx={30}
                    orientation="right"
                    axisLine={false}
                    tickLine={false}
                />
                <YAxis
                    yAxisId="cal"
                    dataKey="calories"
                    hide={true}
                    orientation="left"
                    axisLine={false}
                    tickLine={false}
                /> */}
                <YAxis yAxisId="kg" dataKey="kilogram" orientation='right' name='kilogram' axisLine={false} tickLine={false} tickCount={3} type="number" data="kilogram" tickMargin={50} />
                {/* <YAxis orientation='right' name='kilogram' axisLine={false} tickLine={false} tickCount={3} type="number" data="kilogram" tickMargin={50} /> */}
                {/* <YAxis orientation='right' name='kilogram' axisLine={false} tickLine={false} tickCount={3} type="number" data="kilogram" domain={['dataMin - 20', 'dataMax']} tickMargin={50} /> */}
                <Tooltip
                    animationEasing="ease-out"
                    content={<CustomTooltip />}
                    offset={40}
                />
                <ReferenceLine y={0} stroke="#000" />
                <Bar
                    yAxisId="kg"
                    dataKey="kilogram"
                    fill="#282D30"
                    legendType="cercle"
                    name="&nbsp;Poids(kg)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"
                    radius={[10, 10, 0, 0]}
                // isAnimationActive={true} 
                // animationDuring={2500}
                />

                <Bar
                    yAxisId="cal"
                    dataKey="calories"
                    fill="#E60000"
                    legendType="cercle"
                    name="&nbsp;Calories brûlées(kCal)"
                    radius={[10, 10, 0, 0]}
                />
                {/* <Bar dataKey="kilogram" yAxisId="kg" fill="#282D30" barSize={10} radius={[10, 10, 0, 0]} /> */}
                <Bar dataKey="calories" fill="var(--primary-color)" barSize={10} radius={[10, 10, 0, 0]} />
            </BarChart>
        </ResponsiveContainer>
    );
}

BrushBarChart.propTypes = {
    data: PropTypes.array
}

export default BrushBarChart;
