import './SimpleLineChart.css';
import mock from '../../data/mock.json'
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';


const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
        return (
            <div className="lineChart-tooltip">
                <p className="tooltip-label">{`${payload[0].value} min`}</p>
            </div>
        );
    }
    return null;
};

const renderLegend = () => {
    return <p>Dur√©e moyenne des sessions</p>
}

const CustomTick = ({ index, x, y }) => {
    switch (index) {
        case 0:
            return (
                <text x={x - 5} y={y + 10}>
                    L
                </text>
            );
        case 1:
            return (
                <text x={x - 5} y={y + 10}>
                    M
                </text>
            );
        case 2:
            return (
                <text x={x - 5} y={y + 10}>
                    M
                </text>
            );
        case 3:
            return (
                <text x={x - 5} y={y + 10}>
                    J
                </text>
            );
        case 4:
            return (
                <text x={x - 5} y={y + 10}>
                    V
                </text>
            );
        case 5:
            return (
                <text x={x - 5} y={y + 10}>
                    S
                </text>
            );
        case 6:
            return (
                <text x={x - 5} y={y + 10}>
                    D
                </text>
            );
        default:
            break;
    }
};

function SimpleLineChart() {
    return (
        <ResponsiveContainer width="100%" aspect={1} id="simpleBarChart">
            <LineChart width={300} height={100} data={mock.data.averageSessions}>
                <Legend content={renderLegend} />
                <Line type="monotone" dataKey="sessionLength" stroke="#8884d8" strokeWidth={2} />
                <XAxis dataKey="day" tick={<CustomTick />} tickLine={false} axisLine={false} />
                <Tooltip content={<CustomTooltip />} position={{ y: 0 }} />
            </LineChart>
        </ResponsiveContainer>
    );
}

export default SimpleLineChart;
