import './BrushBarChart.css';
import {
    BarChart,
    Bar,
    ReferenceLine,
    Legend,
    XAxis,
    YAxis,
    Tooltip,
    CartesianGrid,
    ResponsiveContainer,
} from 'recharts';
import PropTypes from 'prop-types';

const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
        return (
            <div className="tooltip">
                <p className="tooltip-label">{`${payload[0].value}Kg`}</p>
                <p className="tooltip-label">{`${payload[1].value}Kcal`}</p>
            </div>
        );
    }
    return null;
};

const CustomTick = ({ index, x, y }) => {
    return (
        <text x={x - 5} y={y + 20}>
            {index + 1}
        </text>
    );
};

function BrushBarChart({ data }) {
    const daysNumbers = () => {

        return data.map((session, index) => index + 1);

    }
    return (
        <div>
            {/* <ResponsiveContainer height={180} width="100%" id="brushBarChart">
                <BarChart
                    data={data}
                    margin={{
                        top: 5,
                        right: 30,
                        left: 20,
                        bottom: 5,
                    }}
                >
                    <CartesianGrid
                        strokeDasharray="2 2"
                        vertical={false}
                    />
                    <XAxis scale='point' tickLine={false} tick={<CustomTick />} stroke='white' />


                    <YAxis orientation='right' name='kilogram' axisLine={false} tickLine={false} tickCount={3} type="number" data="kilogram" tickMargin={50} />
                    <Tooltip
                        animationEasing="ease-out"
                        content={<CustomTooltip />}
                        offset={40}
                    />
                    <ReferenceLine y={0} stroke="#000" />
                    <Bar dataKey="kilogram" fill="#282D30" barSize={10} radius={[10, 10, 0, 0]} />
                    <Bar dataKey="calories" fill="var(--primary-color)" barSize={10} radius={[10, 10, 0, 0]} />
                </BarChart>
            </ResponsiveContainer> */}
            <ResponsiveContainer width="100%" height={180} id="brushBarChart" >

                <BarChart

                    // width={1600}

                    // height={90}

                    data={data}

                    margin={{

                        top: 5,
                        right: 30,
                        left: 20,
                        bottom: 5,

                    }}

                    // barCategoryGap={"13"}

                    maxBarSize={8}

                    barGap={"-100"}

                // padding={{ Right: 20 }}

                >

                    <CartesianGrid strokeDasharray="2 2" vertical={false} />
                    {/* <XAxis scale='point' tickLine={false} tick={<CustomTick />} stroke='white' /> */}

                    <XAxis

                        dataKey={daysNumbers}

                        padding={{ left: -22, right: -22 }}

                        tickLine={false}

                        tickMargin={15}

                    />



                    <YAxis

                        yAxisId="kg"

                        dataKey="kilogram"

                        domain={["dataMin - 2", "dataMax + 1"]}

                        allowDecimals={false}

                        dx={30}

                        orientation="right"

                        axisLine={false}

                        tickLine={false}

                    />

                    <YAxis

                        yAxisId="cal"

                        dataKey="calories"

                        hide={true}

                        orientation="left"

                        axisLine={false}

                        tickLine={false}

                    />



                    <Tooltip
                        animationEasing="ease-out"
                        content={<CustomTooltip />}
                        offset={40}
                    />



                    {/* <text dy={+40} width={200}>

                        Activité quotidienne

                    </text> */}

                    {/* <Legend

                        chartWidth={600}

                        height={80}

                        align='right'

                        verticalAlign='top'

                        margin={{

                            top: 2000,

                            right: 500,

                            left: 300,

                            bottom: 2000

                        }}

                    /> */}



                    <Bar

                        yAxisId="kg"

                        dataKey="kilogram"

                        fill="#282D30"

                        // legendType="cercle"

                        // name="&nbsp;Poids(kg)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"

                        radius={[10, 10, 0, 0]}

                    // isAnimationActive={true}

                    // animationDuring={2500}

                    />



                    <Bar

                        yAxisId="cal"

                        dataKey="calories"

                        fill="var(--primary-color)"

                        // legendType="cercle"

                        // name="&nbsp;Calories brûlées(kCal)"

                        radius={[10, 10, 0, 0]}

                    />



                </BarChart>

            </ResponsiveContainer>
        </div>

    );
}

BrushBarChart.propTypes = {
    data: PropTypes.array
}

export default BrushBarChart;
