import './BrushBarChart.css';
import mock from '../../data/mock.json'
import {
    BarChart,
    Bar,
    ReferenceLine,
    XAxis,
    YAxis,
    Tooltip,
    CartesianGrid,
    ResponsiveContainer,
} from 'recharts';

const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
        return (
            <div className="tooltip">
                <p className="tooltip-label">{`${payload[0].value}Kg`}</p>
                <p className="tooltip-label">{`${payload[1].value}Kcal`}</p>
            </div>
        );
    }
    return null;
};

const CustomTick = ({ index, x, y }) => {
    return (
        <text x={x - 5} y={y + 20}>
            {index + 1}
        </text>
    );
};

function BrushBarChart() {
    return (
        <ResponsiveContainer width="100%" aspect={1} id="brushBarChart">
            <BarChart
                width={820}
                height={300}
                data={mock.data.activity}
                margin={{
                    top: 5,
                    right: 30,
                    left: 20,
                    bottom: 5,
                }}
            >
                <CartesianGrid
                    strokeDasharray="2 2"
                    vertical={false}
                />
                {/* <text x={500 / 2} y={20} fill="black" textAnchor="middle" dominantBaseline="central">
                    <tspan fontSize="14">Title</tspan>
                </text> */}
                <XAxis tickLine={false} tick={<CustomTick />} />
                <YAxis orientation='right' axisLine={false} tickLine={false} tickCount={4} type="number" />
                <Tooltip content={<CustomTooltip />} />
                {/* <Legend verticalAlign="top" wrapperStyle={{ top: 40, right: 20, backgroundColor: '#f5f5f5', borderRadius: 3, lineHeight: '40px' }} /> */}
                <ReferenceLine y={0} stroke="#000" />
                <Bar dataKey="kilogram" fill="#282D30" barSize={5} />
                <Bar dataKey="calories" fill="#E60000" barSize={5} />
            </BarChart>
        </ResponsiveContainer>
        //     <div className="bars-chart">
        //     <div className="bars-chart__container">
        //       <p className="bars-chart__paragraph">Activité quotidienne</p>

        //       <ul className="bars-chart__legend-list">
        //         <li className="bars-chart__legend-item bars-chart__legend-item--dark-grey-dot">
        //           Poids (kg)
        //         </li>
        //         <li className="bars-chart__legend-item bars-chart__legend-item--red-dot">
        //           Calories (Kcal)
        //         </li>
        //       </ul>
        //     </div>
        //     <ResponsiveContainer>
        //       <BarChart data={formattedData} margin={chartsMargins}>
        //         <CartesianGrid
        //           strokeDasharray="2 2"
        //           horizontal={true}
        //           vertical={false}
        //         />
        //         <XAxis dataKey="name" tickLine={false} axisLine={false} />
        //         <YAxis orientation="right" tickLine={false} axisLine={false} />
        //         <Tooltip
        //           animationEasing="ease-out"
        //           content={<BarsCustomTooltip payload={formattedData} />}
        //           offset={40}
        //           wrapperStyle={{ outline: "none" }}
        //         />

        //         <Bar
        //           dataKey="kg"
        //           fill="var(--bg-color-secondary)"
        //           radius={[10, 10, 0, 0]}
        //           barSize={10}
        //         />
        //         <Bar
        //           dataKey="Kcal"
        //           fill="var(--bg-color-primary)"
        //           radius={[10, 10, 0, 0]}
        //           barSize={10}
        //         />
        //       </BarChart>
        //     </ResponsiveContainer>
        //   </div>
    );
}

export default BrushBarChart;
