import './SimpleLineChart.css';
import mock from '../../data/mock.json'
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';


const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
        return (
            <div className="tooltip">
                <p className="tooltip-label">{`${payload[0].value}Kg`}</p>
                <p className="tooltip-label">{`${payload[1].value}Kcal`}</p>
            </div>
        );
    }
    return null;
};

const CustomTick = ({ index, x, y }) => {

    switch (index) {
        case 0:
            return (
                <text x={x - 5} y={y + 10}>
                    {index + 1}
                </text>
            );
            break;
        case 1:
            return (
                <text x={x - 5} y={y + 10}>
                    {index + 1}
                </text>
            );
            break;
        case 2:
            return (
                <text x={x - 5} y={y + 10}>
                    {index + 1}
                </text>
            );
            break;
        case 3:
            return (
                <text x={x - 5} y={y + 10}>
                    {index + 1}
                </text>
            );
            break;
        case 4:
            return (
                <text x={x - 5} y={y + 10}>
                    {index + 1}
                </text>
            );
            break;
        case 5:
            return (
                <text x={x - 5} y={y + 10}>
                    {index + 1}
                </text>
            );
            break;

        default:
            break;
    }


};

const data = [
    {
        name: 'Page A',
        uv: 4000,
        pv: 2400,
        amt: 2400,
    },
    {
        name: 'Page B',
        uv: 3000,
        pv: 1398,
        amt: 2210,
    },
    {
        name: 'Page C',
        uv: 2000,
        pv: 9800,
        amt: 2290,
    },
    {
        name: 'Page D',
        uv: 2780,
        pv: 3908,
        amt: 2000,
    },
    {
        name: 'Page E',
        uv: 1890,
        pv: 4800,
        amt: 2181,
    },
    {
        name: 'Page F',
        uv: 2390,
        pv: 3800,
        amt: 2500,
    },
    {
        name: 'Page G',
        uv: 3490,
        pv: 4300,
        amt: 2100,
    },
];

function SimpleLineChart() {
    return (
        <ResponsiveContainer width="100%" aspect={3} id="simpleBarChart">
            <LineChart width={300} height={100} data={mock.data.averageSessions}>
                <Line type="monotone" dataKey="sessionLength" stroke="#8884d8" strokeWidth={2} />
                <XAxis dataKey="day" tick={<CustomTick />} />
            </LineChart>
            <LineChart
                width={500}
                height={300}
                data={data}
                margin={{
                    top: 5,
                    right: 30,
                    left: 20,
                    bottom: 5,
                }}
            >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="pv" stroke="#8884d8" activeDot={{ r: 8 }} />
                <Line type="monotone" dataKey="uv" stroke="#82ca9d" />
            </LineChart>
        </ResponsiveContainer>
    );
}

export default SimpleLineChart;
