import './BrushBarChart.css';
import mock from '../../data/mock.json'
import {
    BarChart,
    Bar,
    ReferenceLine,
    XAxis,
    YAxis,
    Tooltip,
    CartesianGrid,
    ResponsiveContainer,
} from 'recharts';

const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
        return (
            <div className="tooltip">
                <p className="tooltip-label">{`${payload[0].value}Kg`}</p>
                <p className="tooltip-label">{`${payload[1].value}Kcal`}</p>
            </div>
        );
    }
    return null;
};

function BrushBarChart() {
    return (
        <ResponsiveContainer width={820} height={320} id="brushBarChart">
            <BarChart
                width={820}
                height={300}
                data={mock.data.activity}
                margin={{
                    top: 5,
                    right: 30,
                    left: 20,
                    bottom: 5,
                }}
            >
                <CartesianGrid stroke="#ccc" strokeDasharray="3 0" />
                {/* <text x={500 / 2} y={20} fill="black" textAnchor="middle" dominantBaseline="central">
                    <tspan fontSize="14">Title</tspan>
                </text> */}
                <XAxis />
                <YAxis orientation='right' />
                <YAxis domain={[69, 'auto']} />
                <YAxis domain={[0, 'dataMax + 10']} />
                <Tooltip content={<CustomTooltip />} />
                {/* <Legend verticalAlign="top" wrapperStyle={{ top: 40, right: 20, backgroundColor: '#f5f5f5', borderRadius: 3, lineHeight: '40px' }} /> */}
                <ReferenceLine y={0} stroke="#000" />
                <Bar dataKey="kilogram" fill="#282D30" barSize={5} />
                <Bar dataKey="calories" fill="#E60000" barSize={5} />
            </BarChart>
        </ResponsiveContainer>
    );
}

export default BrushBarChart;
